css:
.nuclide-datatip-overlay {
  background: #ccdce0;
  box-shadow: 0px 1px 4px 0px #000000;
  color: #6796a2;
  cursor: -webkit-grab;
  font-family: Menlo, Monaco, Consolas, monospace;
  font-size: 12px;
  margin-top: -2px;
  position: relative;
  white-space: nowrap;
}
.nuclide-datatip-dragging {
  cursor: -webkit-grabbing;
}
.nuclide-datatip-container {
  background-color: #e6eeef;
  display: flex;
  position: relative;
  transition: background-color 0.15s ease;
}
.nuclide-datatip-container:hover {
  background-color: rgba(224, 234, 236, 0.54);
}
.nuclide-datatip-content {
  flex-grow: 1;
}
.nuclide-datatip-content .list-tree {
  cursor: inherit;
}
.nuclide-datatip-pin-button {
  align-items: center;
  border-left: 1px solid rgba(19, 29, 32, 0.1);
  cursor: pointer;
  display: flex;
  line-height: 2;
  margin: 0.1em 0 0 0;
  padding-left: 0.5em;
}
.nuclide-datatip-pin-button:hover {
  background-color: rgba(255, 255, 255, 0.05);
  color: #131d20;
}
atom-text-editor::shadow .nuclide-datatip-highlight-region > .region {
  background: rgba(97, 146, 158, 0.08);
}
atom-text-editor::shadow .nuclide-datatip-highlight-region-active > .region {
  animation: RotatingBackground 1s ease-in-out infinite reverse;
  background: linear-gradient(90deg, rgba(97, 146, 158, 0.08), #e6eeef, rgba(97, 146, 158, 0.08), #e6eeef);
  background-size: 300% 100%;
}
