css:
.nuclide-home {
  display: flex;
  font-size: 1.25em;
  line-height: 1.4;
  color: #444444;
  background-color: #f4f4f4;
  overflow-x: auto;
}
.nuclide-home a {
  color: #5293d8;
}
.nuclide-home-container {
  width: 100%;
  max-width: 450px;
  min-width: 300px;
  margin: auto;
  padding: 3em 2em;
}
.nuclide-home-title {
  font-size: 1.4em;
  text-align: center;
  line-height: 1.3;
  margin: 1em 0;
}
.nuclide-home-card {
  margin: 1em 0;
  border-radius: 4px;
  border: 1px solid #9f9f9f;
  background-color: #fcfcfc;
}
.nuclide-home-summary {
  padding: 1em 1.5em;
  font-size: 1.1em;
  font-weight: 300;
  line-height: 1.2;
  cursor: pointer;
}
.nuclide-home-summary:hover {
  color: #4e4e4e;
}
.nuclide-home-summary::-webkit-details-marker {
  display: none;
}
.nuclide-home-summary:before {
  width: 20px;
  color: #999999;
}
details[open] .nuclide-home-summary:before {
  color: #000000;
}
details[open] .nuclide-home-summary {
  color: #000000;
}
.nuclide-home-detail {
  border-top: 1px solid #9f9f9f;
  padding: 1.5em;
}
.nuclide-home-detail .welcome-note {
  margin-bottom: 0;
}
.nuclide-home-logo {
  height: 180px;
  margin: 0 auto 2em;
  width: 150px;
}
/* Contain the horizontal pair of sections for the welcome and feature list. */
.nuclide-home-containers {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-around;
  width: 100%;
}
/* Rebases button size in the enlarged detail summary. */
.btn.btn-sm.nuclide-home-tryit {
  line-height: 1.8em;
  margin-top: -0.3em;
}
