css:
.nuclide-task-runner-toolbar {
  flex: 1;
  border-bottom: 1px solid #181a1f;
  overflow: hidden;
}
.nuclide-task-runner-progress-bar {
  width: 100%;
  height: 2px;
  position: absolute;
  bottom: 0;
}
.nuclide-task-runner-progress-bar[hidden] {
  display: none;
}
.nuclide-task-runner-progress-bar.indeterminate {
  background-image: repeating-linear-gradient(90deg, #0088ff 0%, #0088ff 25%, transparent 25%, transparent 100%);
  background-size: 1000px 100%;
  animation: animate-indeterminate-bar 8s linear infinite;
}
.nuclide-task-runner-progress-bar.indeterminate:before {
  content: ' ';
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: repeating-linear-gradient(90deg, rgba(0, 136, 255, 0.3) 0%, rgba(0, 136, 255, 0.3) 25%, transparent 25%, transparent 100%);
  background-size: 1800px 100%;
  animation: animate-indeterminate-bar-2 8s linear infinite;
}
@keyframes animate-indeterminate-bar {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 1000px 0;
  }
}
@keyframes animate-indeterminate-bar-2 {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 1800px 0;
  }
}
.nuclide-task-runner-progress-bar-bar {
  position: absolute;
  height: 100%;
  left: 0;
  width: 0;
  transition: width 0.3s ease-out;
  background-color: #0088ff;
}
.nuclide-task-runner-toolbar-contents {
  display: flex;
  align-items: center;
}
.nuclide-task-runner-system-icon-wrapper {
  display: flex;
  align-items: center;
  height: 15px;
  font-variant: small-caps;
  text-transform: lowercase;
}
.nuclide-task-runner-system-icon-wrapper .icon-path-fill {
  fill: #9da5b4;
}
.nuclide-task-runner-system-task-button,
.nuclide-task-runner-system-task-icon,
.nuclide-task-runner-system-task-icon::before {
  display: flex;
  align-items: center;
}
.nuclide-task-runner-system-task-button-divider {
  height: 10px;
  width: 1px;
  background-color: #181a1f;
  display: flex;
  margin-right: 8px;
  margin-left: 8px;
}
.nuclide-task-runner-system-task-icon::before {
  font-size: 11px;
  width: auto;
}
.nuclide-task-runner-tool-bar-button[hidden] {
  display: none;
}
.nuclide-task-runner-tool-bar-button[hidden] {
  display: none;
}
